import React, { Component } from 'react'
import { connect } from 'react-redux'
import { FormControl, FormGroup } from 'react-bootstrap'
import { Link } from 'react-router-dom'

import { login } from '../Actions'

class SignIn extends Component {
  constructor(props) {
    super(props)

    this.state = {
      username: '',
      password: '',
      submitted: false,
      token: ''
    }

    this.handleChange = this.handleChange.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)
  }

  handleChange(e) {
    const { name, value } = e.target
    this.setState({ [name]: value })
  }

  handleSubmit(event) {
    event.preventDefault()
    this.setState({ submitted: true })
    const { username, password } = this.state
    const { dispatch } = this.props
    if (username && password) dispatch(login(username, password))
  }

  render() {
    const { loggingIn } = this.props
    const { username, password, submitted } = this.state;
    return (
      <div className="col-sm-4">
        <h2>Sign In</h2>
        <form>
          <FormGroup>
            <FormControl
              className={'form-group' + (submitted && !username ? ' has-error' : '')}
              onChange={this.handleChange}
              placeholder="Username"
              name="username"
              value={username}
            />
            {submitted && !username && (<div className="help-block">Username is required</div>)}
            <FormControl
              className={'form-group' + (submitted && !password ? ' has-error' : '')}
              onChange={this.handleChange}
              placeholder="Password"
              name="password"
              value={password}
              type='password'
            />
            {submitted && !password && (<div className="help-block">Password is required</div>)}
          </FormGroup>
          <div className="buttons_layout">
            <button onClick={this.handleSubmit}>Sign In</button>
            <Link to="/forgotPassword">Forgot Password</Link>
          </div>
          {loggingIn && (
            <img
              alt="loading"
              id="loading"
              src="data:image/gif;base64,"
            />
          )}
        </form>
      </div>
    )
  }
}

const mapStateToProps = state => {
  const { loggingIn } = state.authentication
  return {
    loggingIn
  }
}

const connectedSignIn = connect(mapStateToProps)(SignIn)
export { connectedSignIn as SignIn }
